import React, { useState } from 'react';
import QRCode from 'qrcode.react';

function App() {
  const [amount, setAmount] = useState('');
  const [qrCodeData, setQRCodeData] = useState('');

  const generateQRCode = () => {
    if (amount) {
      // Generate the QR code data based on the amount input
      const qrCodeData = "http://localhost:3000/pay?" + "amount=" + `${amount}` + "&" + "iban=" + `${iban}` + "companyName=" + `${companyName}`
      //const qrCodeData = `Amount: ${amount}`;
      setQRCodeData(qrCodeData);
    }
  };

  return (
    <div className="App">
      <h1>Payflow</h1>
      <div>
        <label htmlFor="amount">Enter the amount:</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="iban">Enter the IBAN:</label>
        <input
          type="text"
          id="iban"
          value={iban}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="companyName">Enter the company name:</label>
        <input
          type="text"
          id="companyName"
          value={companyName}
          onChange={(e) => setAmount(e.target.value)}
        />
        <button onClick={generateQRCode}>Generate QR Code</button>
      </div>
      {qrCodeData && (
        <div>
          <h2>QR Code:</h2>
          <QRCode value={qrCodeData} size={200} />
        </div>
      )}
    </div>
  );
}

export default App;
